        // debugger;

        document.title = `You push the Button ${counter} times`;

    })

    return (
        <div style={{margin: '40px'}}>
            <h1 style={{margin: '40px 0'}}>hello</h1>
            {counter > 0 ? <p>you pushed {counter} times</p> : <p>&nbsp;</p>}
            <button onClick={()=>setCounter(++counter)}>push me</button>
        </div>

    )
}


