import s from './AddExpenseForm.module.scss';
import Input from './Input';
import React, {useState} from 'react';


export default function AddExpenseForm(props){

    let formValue = {};
    let dontSendValue = false;
    const [inputsAlert, setInputsAlert] = useState({name: false, cost: false, date: false});

    const submitHandler = (e) => {
        e.preventDefault();

        let formData = new FormData(e.target);

        for( var el of formData.entries()){
            const [key, value] = el;

            if(value === ''){

                setInputsAlert(inputsAlert=>({...inputsAlert, [key]: true}));
                dontSendValue = true;

            }

            formValue[key] = value;
        }

        if(!dontSendValue) {
            props.handler(formValue);
            setInputsAlert({name: false, cost: false, date: false})
        }

    }

    return (
        <form className={s.form} id="_id_add_expense_form" onSubmit={submitHandler}>
            <div className={s.form_item}>
                <label>Enter the name of expense</label>
                <Input type="text" name="name" alert={inputsAlert.name}/>
            </div>
            <div className={s.form_item}>
                <label>Enter the cost</label>
                <Input type="number" name="cost"  alert={inputsAlert.cost}/>
            </div>
            <div className={s.form_item}>
                <label>Enter the date of expense</label>
                <Input type="date" name="date"  alert={inputsAlert.date}/>
            </div>
            <input type="submit" value="add" />
        </form>
    )
}
