import {useState, useEffect, useRef} from 'react';
import s from './AddExpenseForm.module.scss';

export default function Input(props){

    // let isAlert;
    const [inputValue, setInputValue] = useState('');
    // const [inputAlert, setInputAlert] = useState(props.alert);

    const inputRef = useRef(null);

    console.log('props.alert ' + props.alert);
    // console.log('inputAlert ' + inputAlert);
    // console.log('isAlert ' + isAlert);

    useEffect(()=>{
        inputRef.current.className = props.alert ? s.red : '';
    },[props.alert])

    const nameHandler = (e)=>{
        console.dir(inputRef);
        if(inputRef.current.className!==''){
            inputRef.current.className = '';
        }
        setInputValue(e.target.value);
        // setInputAlert(false);

        // isAlert = false;

        // if(props.alert && e.target.value!==''){

        //     console.log('nameHandler inputAlert ');
        //     // setInputAlert(false)
        //     // console.log(inputAlert);
        // }
    }

    return (
        <input  className={props.alert ? s.red : ''}
                ref={inputRef}
                type={props.type}
                name={props.name}
                data-check={props.alert}
                value={inputValue}
                onChange={nameHandler}
                onFocus={nameHandler}
                />
    )

}
