import s from './AddExpenseForm.module.scss';
import {useState} from 'react';


export default function AddExpenseForm(props){

    // console.dir(s);

    let [inputNameMarker, setInputNameMarker] = useState('');
    let [inputCostMarker, setInputCostMarker] = useState('');
    let [inputDateMarker, setInputDateMarker] = useState('');


    const submitHandler = (e) => {
        e.preventDefault();

        // process the form
        // create expense item and push to parent
        // use props.handler

        let inputName = e.target.name.value;
        let inputCost = e.target.cost.value;
        let inputDate = e.target.date.value;
        console.log(inputName);

        if(inputName==''){
            setInputNameMarker('red');
            setTimeout(()=>{
                setInputNameMarker('');
            }, 1000);
        }
        if(!inputCost){
            setInputCostMarker('red');
            setTimeout(()=>{
                setInputCostMarker('');
            }, 1000);
        }
        if(!inputDate){
            setInputDateMarker('red');
            setTimeout(()=>{
                setInputDateMarker('');
            }, 1000);
        }

    }

    return (
        <form className={s.form} id="_id_add_expense_form" onSubmit={submitHandler}>
            <div className={s.form_item}>
                <label htmlFor="_id_add_expense_form-name">Enter the name of expense</label>
                <input className={inputNameMarker} type="text" name="name" id="_id_add_expense_form-name"/>
            </div>
            <div className={s.form_item}>
                <label htmlFor="_id_add_expense_form-cost">Enter the cost</label>
                <input className={inputCostMarker} type="number" min="0.01" step="0.01" name="cost" id="_id_add_expense_form-cost"/>
            </div>
            <div className={s.form_item}>
                <label htmlFor="_id_add_expense_form-date">Enter the date of expense</label>
                <input className={inputDateMarker} type="date" name="date" id="_id_add_expense_form-date"/>
            </div>
            <input type="submit" value="add" />
        </form>
    )
}
