import {useState, useEffect} from 'react';
import s from './AddExpenseForm.module.scss';

export default function Input(props){

    let isAlert = props.alert;
    const [inputValue, setInputValue] = useState('');
    const [inputAlert, setInputAlert] = useState(props.alert);


    console.log('propsAlert ' + props.name);
    console.log(props.alert);

    useEffect(()=>{
        setInputAlert(props.alert);
        isAlert = props.alert;
        // console.log('useEffect ' + props.name);
        // console.dir(inputAlert);
    }, [props.alert])

    const nameHandler = (e)=>{

        setInputValue(e.target.value);

        if(props.alert && e.target.value!==''){

            console.log('nameHandler inputAlert ');
            // setInputAlert(false)
            console.log(inputAlert);
        }
    }

    return (
        <input  className={inputAlert ? s.red : ''}
                type={props.type}
                name={props.name}
                data-check={props.alert}
                value={inputValue}
                onChange={nameHandler}
                onFocus={nameHandler}
                />
    )

}
