import {useState} from 'react';
import s from './AddExpenseForm.module.scss';

export default function Input(props){

    const [inputValue, setInputValue] = useState('');
    const [inputAlert, setInputAlert] = useState(props.alert ? true : false);
    const nameHandler = (e)=>{
        if(inputAlert){
            setInputAlert(false)
        }
        setInputValue(e.target.value);
    }

    return (
        <input  className={inputAlert ? s.red : ''}
                type={props.type}
                name={props.name}
                value={inputValue}
                onChange={nameHandler}
                onFocus={nameHandler}
                />
    )

}
