import s from './AddExpenseForm.module.scss';
import {useState} from 'react';


export default function AddExpenseForm(props){


    // set consts and handler for inputName
        const [nameValue, setNameValue] = useState('');
        const [nameAlert, setNameAlert] = useState(false);
        const nameHandler = (e)=>{
            if(nameAlert){
                setNameAlert(false)
            }
            setNameValue(e.target.value);
        }

    // set consts and handler for inputName
        const [costValue, setCostValue] = useState('');
        const [costAlert, setCostAlert] = useState(false);
        const costHandler = (e)=>{
            if(costAlert){
                setCostAlert(false)
            }
            setCostValue(e.target.value);
        }

    const submitHandler = (e) => {
        e.preventDefault();
        if(nameValue===''){
            setNameAlert(true);
            setTimeout(()=>{
                setNameAlert(false);
            }, 700)

        }
        if(costValue===''){
            setCostAlert(true);
            setTimeout(()=>{
                setCostAlert(false);
            }, 700)

        }
    }

    return (
        <form className={s.form} id="_id_add_expense_form" onSubmit={submitHandler}>
            <div className={s.form_item}>
                <label>Enter the name of expense</label>
                <input  className={nameAlert ? s.red : ''}
                        type="text"
                        name="name"
                        value={nameValue}
                        onChange={nameHandler}
                        onFocus={nameHandler}
                        />
            </div>
            <div className={s.form_item}>
                <label>Enter the cost</label>
                <input  className={costAlert ? s.red : ''}
                        type="number"
                        min="0"
                        name="cost"
                        value={costValue}
                        onChange={costHandler}
                        onFocus={costHandler}
                        />
            </div>
            {/* <div className={s.form_item}>
                <label>Enter the date of expense</label>
                <input  className={dateAlert ? s.red : ''}
                        type="date"
                        name="date"
                        />
            </div> */}
            <input type="submit" value="add" />
        </form>
    )
}
