import s from './AddExpenseForm.module.scss';
import Input from './Input';
import React, {useState} from 'react';


export default function AddExpenseForm(props){

    let formValue = {};

    // const [inputsAlert, setInputsAlert] = useState({name: false, cost: false, date: false});
    let [nameAlert, setNameAlert] = useState(false);

    const submitHandler = (e) => {
        e.preventDefault();

        // console.dir(e);

        let formData = new FormData(e.target);

        for( var el of formData.entries()){
            const [key, value] = el;

            setNameAlert(true);

            formValue[key] = value;
        }
        // setState(state => ({ ...state, left: 100, top: 200 }));

        // setTimeout(()=>{
        //     console.dir(inputsAlert.name);
        // }, 1000)


        // console.dir(inputsAlert);
        // console.dir(formData);
    }

    return (
        <form className={s.form} id="_id_add_expense_form" onSubmit={submitHandler}>
            <div className={s.form_item}>
                <label>Enter the name of expense</label>
                <Input type="text" name="name" alert={nameAlert}/>
            </div>
            <div className={s.form_item}>
                <label>Enter the cost</label>
                <Input type="number" name="cost" />
            </div>
            <div className={s.form_item}>
                <label>Enter the date of expense</label>
                <Input type="date" name="date" />
            </div>
            <input type="submit" value="add" />
        </form>
    )
}
